import sys

data = sys.stdin.read().strip().splitlines()
if not data:
    sys.exit(0)

def longest_repeated_substring(s):
    n = len(s)
    for L in range(n-1, 0, -1):
        seen = set()
        for i in range(n - L + 1):
            sub = s[i:i+L]
            if sub in seen:
                return L
            seen.add(sub)
    return 0

outs = []
for line in data:
    line = line.strip()
    if line == '':
        continue
    outs.append(str(longest_repeated_substring(line)))

sys.stdout.write("\n".join(outs))

