#!/usr/bin/env python3
import sys
from collections import deque

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it)); m = int(next(it))
    g = [[] for _ in range(n)]
    for _ in range(m):
        a = int(next(it)) - 1
        b = int(next(it)) - 1
        g[a].append(b)
        g[b].append(a)
    color = [-1]*n
    is_bip = True
    parts = []  # list of (cnt0,cnt1) per component
    for i in range(n):
        if color[i] != -1:
            continue
        q = deque([i])
        color[i] = 0
        cnt0 = 0
        cnt1 = 0
        while q:
            u = q.popleft()
            if color[u] == 0:
                cnt0 += 1
            else:
                cnt1 += 1
            for v in g[u]:
                if color[v] == -1:
                    color[v] = color[u]^1
                    q.append(v)
                elif color[v] == color[u]:
                    is_bip = False
        parts.append((cnt0, cnt1))
    if not is_bip:
        print("0 1")
        return
    # sum of intra-part pairs
    sum_pairs = 0
    for a,b in parts:
        sum_pairs += a*(a-1)//2
        sum_pairs += b*(b-1)//2
    if sum_pairs > 0:
        print("1 {}".format(sum_pairs))
        return
    # no intra-part pairs possible
    if m == 0:
        if n < 3:
            print("0 0")
        else:
            ways = n*(n-1)*(n-2)//6
            print("3 {}".format(ways))
        return
    # otherwise
    if n < 3:
        print("0 0")
        return
    ways = m * (n-2)
    print("2 {}".format(ways))

if __name__ == "__main__":
    main()
