from collections import deque

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it)); m = int(next(it))
adj = [[] for _ in range(n)]
for _ in range(m):
    a = int(next(it)) - 1
    b = int(next(it)) - 1
    adj[a].append(b)
    adj[b].append(a)

color = [-1]*n
visited = [False]*n
odd_cycle = False
sum_choose = 0
comp_sizes = []

for i in range(n):
    if not visited[i]:
        q = deque([i])
        visited[i] = True
        color[i] = 0
        cnt = [0,0]
        cnt[0] = 1
        nodes = [i]
        while q:
            u = q.popleft()
            for v in adj[u]:
                if color[v] == -1:
                    color[v] = color[u]^1
                    cnt[color[v]] += 1
                if color[v] == color[u]:
                    odd_cycle = True
                if not visited[v]:
                    visited[v] = True
                    q.append(v)
                    nodes.append(v)
        comp_sizes.append(len(nodes))
        if odd_cycle:
            break
        sum_choose += cnt[0]*(cnt[0]-1)//2
        sum_choose += cnt[1]*(cnt[1]-1)//2

if odd_cycle:
    print("0 1")
    sys.exit(0)

if sum_choose > 0:
    print("1 {}".format(sum_choose))
    sys.exit(0)

x = 0
y = 0
for sz in comp_sizes:
    if sz == 1:
        y += 1
    elif sz == 2:
        x += 1

if x == 0:
    # all isolated
    if n < 3:
        print("0 0")
    else:
        from math import comb
        print("3 {}".format(comb(n,3)))
else:
    if y > 0:
        print("2 {}".format(x * y))
    else:
        # only K2 components
        if x < 2:
            print("0 0")
        else:
            print("2 {}".format(2 * x * (x - 1)))

