#!/usr/bin/env python3
import sys
from collections import deque

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    m = int(next(it))
    adj = [[] for _ in range(n)]
    for _ in range(m):
        u = int(next(it)) - 1
        v = int(next(it)) - 1
        if u == v:
            continue
        adj[u].append(v)
        adj[v].append(u)

    color = [-1] * n
    comps = []
    # Check bipartiteness and collect color class sizes
    for i in range(n):
        if color[i] != -1:
            continue
        q = deque()
        q.append(i)
        color[i] = 0
        cnt0 = 0
        cnt1 = 0
        while q:
            u = q.popleft()
            if color[u] == 0:
                cnt0 += 1
            else:
                cnt1 += 1
            for v in adj[u]:
                if color[v] == -1:
                    color[v] = color[u] ^ 1
                    q.append(v)
                elif color[v] == color[u]:
                    # non-bipartite
                    print("0 1")
                    return
        comps.append((cnt0, cnt1))

    # If any component has a color class size >= 2 (i.e., contributes pairs), then t=1
    pairs_sum = 0
    for a, b in comps:
        if a >= 2:
            pairs_sum += a * (a - 1) // 2
        if b >= 2:
            pairs_sum += b * (b - 1) // 2

    if pairs_sum > 0:
        print("1", pairs_sum)
        return

    # All components have sizes <=2, i.e., each component is isolated vertex or a single edge
    if m == 0:
        # need to add 3 edges to make a triangle
        if n < 3:
            print("3 0")
        else:
            ways = n * (n - 1) * (n - 2) // 6
            print("3", ways)
    else:
        # m > 0, can do with 2 edges: choose an existing edge and any other vertex
        ways = m * (n - 2)
        print("2", ways)

if __name__ == "__main__":
    main()

