import sys

data = sys.stdin.read().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
l = list(next(it).strip())
r = list(next(it).strip())

left_pos = [[] for _ in range(26)]
right_pos = [[] for _ in range(26)]
left_q = []
right_q = []

for i, ch in enumerate(l, start=1):
    if ch == '?':
        left_q.append(i)
    else:
        left_pos[ord(ch) - 97].append(i)

for i, ch in enumerate(r, start=1):
    if ch == '?':
        right_q.append(i)
    else:
        right_pos[ord(ch) - 97].append(i)

res = []

# match same letters
for c in range(26):
    L = left_pos[c]
    R = right_pos[c]
    while L and R:
        res.append((L.pop(), R.pop()))

# match left specific with right '?'
for c in range(26):
    L = left_pos[c]
    while L and right_q:
        res.append((L.pop(), right_q.pop()))

# match left '?' with right specific
for c in range(26):
    R = right_pos[c]
    while R and left_q:
        res.append((left_q.pop(), R.pop()))

# match '?' with '?'
while left_q and right_q:
    res.append((left_q.pop(), right_q.pop()))

out = []
out.append(str(len(res)))
for a, b in res:
    out.append(f"{a} {b}")
sys.stdout.write("\n".join(out))

