import sys

def main():
    data = sys.stdin.read().splitlines()
    if not data: 
        return
    it = iter(data)
    n = int(next(it).strip())
    l = next(it).strip()
    r = next(it).strip()
    left_pos = {chr(ord('a')+i): [] for i in range(26)}
    right_pos = {chr(ord('a')+i): [] for i in range(26)}
    left_q = []
    right_q = []
    for i,ch in enumerate(l, start=1):
        if ch == '?':
            left_q.append(i)
        else:
            left_pos[ch].append(i)
    for i,ch in enumerate(r, start=1):
        if ch == '?':
            right_q.append(i)
        else:
            right_pos[ch].append(i)
    pairs = []
    # match same letters
    for c in left_pos:
        lp = left_pos[c]
        rp = right_pos[c]
        m = min(len(lp), len(rp))
        for i in range(m):
            pairs.append((lp[i], rp[i]))
        # remove used
        left_pos[c] = lp[m:]
        right_pos[c] = rp[m:]
    # match left specific with right question
    for c in left_pos:
        for idx in left_pos[c]:
            if not right_q:
                break
            pairs.append((idx, right_q.pop()))
        left_pos[c] = []
    # match left question with right specific
    for c in right_pos:
        for idx in right_pos[c]:
            if not left_q:
                break
            pairs.append((left_q.pop(), idx))
        right_pos[c] = []
    # match remaining question-question
    while left_q and right_q:
        pairs.append((left_q.pop(), right_q.pop()))
    # output
    out = []
    out.append(str(len(pairs)))
    for a,b in pairs:
        out.append(f"{a} {b}")
    sys.stdout.write("\n".join(out))

if __name__ == "__main__":
    main()

