import sys

def solve_case(n, x, intervals, M=200000):
    INF = 10**30
    ans = INF
    # prepare ends_at
    ends_at = [[] for _ in range(M+3)]
    for l, r, c in intervals:
        ends_at[r].append((l, r, c))
    # sort intervals by l
    intervals_by_l = sorted(intervals, key=lambda t: t[0])
    min_cost_len = [INF] * (x + 1)
    cur_end = 0
    for l, r, c in intervals_by_l:
        # process ends with end < l
        while cur_end < l:
            for (ll, rr, cc) in ends_at[cur_end]:
                length = rr - ll + 1
                if length <= x:
                    if cc < min_cost_len[length]:
                        min_cost_len[length] = cc
            cur_end += 1
        length2 = r - l + 1
        need = x - length2
        if 1 <= need <= x and min_cost_len[need] < INF:
            total = c + min_cost_len[need]
            if total < ans:
                ans = total
    return ans

def reflect_intervals(intervals, M=200000):
    reflected = []
    for l, r, c in intervals:
        nl = M - r
        nr = M - l
        reflected.append((nl, nr, c))
    return reflected

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    outputs = []
    M = 200000
    try:
        while True:
            n = int(next(it))
            x = int(next(it))
            intervals = []
            for _ in range(n):
                l = int(next(it)); r = int(next(it)); c = int(next(it))
                intervals.append((l, r, c))
            ans1 = solve_case(n, x, intervals, M)
            refl = reflect_intervals(intervals, M)
            ans2 = solve_case(n, x, refl, M)
            ans = min(ans1, ans2)
            outputs.append(str(ans if ans < 10**30 else -1))
    except StopIteration:
        pass
    sys.stdout.write("\n".join(outputs))

if __name__ == "__main__":
    main()

