import sys
from bisect import bisect_right, bisect_left

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
it = iter(data)
n = next(it)
x = next(it)
intervals = []
max_coord = 0
for _ in range(n):
    l = next(it); r = next(it); c = next(it)
    intervals.append((l, r, c))
    if r > max_coord: max_coord = r
    if l > max_coord: max_coord = l

from collections import defaultdict

# For each length, map end -> min cost, and start -> min cost
ends_map = defaultdict(dict)
starts_map = defaultdict(dict)

for l, r, c in intervals:
    length = r - l + 1
    d = ends_map[length]
    if r in d:
        if c < d[r]:
            d[r] = c
    else:
        d[r] = c
    s = starts_map[length]
    if l in s:
        if c < s[l]:
            s[l] = c
    else:
        s[l] = c

# For each length build sorted ends with prefix mins and sorted starts with suffix mins
ends_lists = {}
ends_prefix_min = {}
starts_lists = {}
starts_suffix_min = {}

for length, mp in ends_map.items():
    items = sorted(mp.items())  # list of (end, cost) sorted by end
    ends = [e for e, _ in items]
    costs = [c for _, c in items]
    pref = []
    cur = 10**30
    for c in costs:
        if c < cur: cur = c
        pref.append(cur)
    ends_lists[length] = ends
    ends_prefix_min[length] = pref

for length, mp in starts_map.items():
    items = sorted(mp.items())  # (start, cost)
    starts = [s for s, _ in items]
    costs = [c for _, c in items]
    suf = [0]*len(costs)
    cur = 10**30
    for i in range(len(costs)-1, -1, -1):
        if costs[i] < cur: cur = costs[i]
        suf[i] = cur
    starts_lists[length] = starts
    starts_suffix_min[length] = suf

INF = 10**30
ans = INF

for l, r, c in intervals:
    len1 = r - l + 1
    need = x - len1
    if need <= 0:
        continue
    # check intervals of length need that end < l
    if need in ends_lists:
        ends = ends_lists[need]
        pref = ends_prefix_min[need]
        idx = bisect_right(ends, l-1) - 1
        if idx >= 0:
            val = pref[idx]
            if val + c < ans:
                ans = val + c
    # check intervals of length need that start > r
    if need in starts_lists:
        starts = starts_lists[need]
        suf = starts_suffix_min[need]
        idx = bisect_left(starts, r+1)
        if idx < len(starts):
            val = suf[idx]
            if val + c < ans:
                ans = val + c

if ans >= INF:
    print(-1)
else:
    print(ans)

