import sys

def solve_case(data_iter):
    try:
        n = int(next(data_iter))
    except StopIteration:
        return None
    x = int(next(data_iter))
    intervals = []
    max_r = 0
    for _ in range(n):
        l = int(next(data_iter)); r = int(next(data_iter)); c = int(next(data_iter))
        intervals.append((l, r, c))
        if r > max_r: max_r = r
    # group intervals by start and by end
    starts = {}
    ends = [[] for _ in range(max_r + 2)]
    for l, r, c in intervals:
        starts.setdefault(l, []).append((l, r, c))
        ends[r].append((l, r, c))
    INF = 10**30
    max_len = max_r  # safe upper bound
    best = [INF] * (max_len + 2)  # best[length] minimal cost for intervals of that length ended before current start
    ans = INF
    cur_r = 0
    # iterate start days in increasing order where starts exist
    all_start_days = sorted(starts.keys())
    for s in all_start_days:
        # process ends with r < s
        while cur_r < s:
            # update best using intervals ending at cur_r
            for l, r, c in ends[cur_r]:
                length = r - l + 1
                if c < best[length]:
                    best[length] = c
            cur_r += 1
        # now consider intervals starting at s, try pair with previously seen best
        for l, r, c in starts[s]:
            length = r - l + 1
            need = x - length
            if 1 <= need <= max_len and best[need] < INF:
                total = c + best[need]
                if total < ans:
                    ans = total
    if ans == INF:
        return -1
    return ans

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    outputs = []
    while True:
        res = solve_case(it)
        if res is None:
            break
        outputs.append(str(res))
    sys.stdout.write("\n".join(outputs))

if __name__ == "__main__":
    main()

