import sys

data = sys.stdin.read().strip().split()
it = iter(data)
n = int(next(it))
x = int(next(it))
vouchers = []
maxR = 0
for _ in range(n):
    l = int(next(it)); r = int(next(it)); c = int(next(it))
    d = r - l + 1
    vouchers.append((l, r, d, c))
    if r > maxR: maxR = r

# We'll process by durations. For each duration len, maintain minimal cost of interval ending at or before position p,
# and similarly starting at or after position p.
INF = 10**30
# coordinate limit up to maxR (<=2e5)
M = maxR + 5
best_left = [INF] * (M + 2)  # best_left[pos] = minimal cost of any interval with given duration that ends at pos
best_by_len_left = dict()  # will store for each len an array of prefix minima
# Group vouchers by duration
by_len = {}
for l, r, d, c in vouchers:
    by_len.setdefault(d, []).append((l, r, c))

# For each duration, build prefix min of costs by end position
pref_min = {}
suff_min = {}
for d, arr in by_len.items():
    # min cost for intervals ending at position i
    end_min = [INF] * (M + 2)
    start_min = [INF] * (M + 2)
    for l, r, c in arr:
        if c < end_min[r]:
            end_min[r] = c
        if c < start_min[l]:
            start_min[l] = c
    # build prefix minima for ends (so for any pos we can get min cost among intervals with r <= pos)
    for i in range(1, M+1):
        if end_min[i] < end_min[i-1]:
            end_min[i] = end_min[i]
        else:
            end_min[i] = end_min[i-1]
    # build suffix minima for starts (for any pos we can get min cost among intervals with l >= pos)
    for i in range(M, 0, -1):
        if start_min[i] < start_min[i+1]:
            start_min[i] = start_min[i]
        else:
            start_min[i] = start_min[i+1]
    pref_min[d] = end_min
    suff_min[d] = start_min

ans = INF
# For each voucher i consider it as the first; need partner of duration x - d that doesn't intersect.
for l, r, d, c in vouchers:
    need = x - d
    if need <= 0:
        continue
    if need not in pref_min:
        continue
    # partner ending before l: r_partner < l -> partner.r <= l-1 => use pref_min[need][l-1]
    left_cost = pref_min[need][l-1] if l-1 >= 0 else INF
    if left_cost < INF:
        total = c + left_cost
        if total < ans:
            ans = total
    # partner starting after r: partner.l > r -> partner.l >= r+1 => use suff_min[need][r+1]
    right_cost = suff_min[need][r+1] if r+1 <= M else INF
    if right_cost < INF:
        total = c + right_cost
        if total < ans:
            ans = total

print(-1 if ans >= INF else ans)

