            end_best[r] = c
        if c < start_best[l]:
            start_best[l] = c
    # propagate prefix minima for end_best (best ending at or before i)
    for i in range(1, LIMIT):
        if end_best[i-1] < end_best[i]:
            end_best[i] = end_best[i-1]
    # propagate suffix minima for start_best (best starting at or after i)
    for i in range(LIMIT-2, -1, -1):
        if start_best[i+1] < start_best[i]:
            start_best[i] = start_best[i+1]
    pre_end[d] = end_best
    pre_start[d] = start_best

# Now iterate vouchers as first interval, try to find complementary duration non-overlapping
for l, r, c in vouchers:
    d1 = r - l + 1
    d2 = x - d1
    if d2 <= 0:
        continue
    # complementary duration must exist
    if d2 not in pre_end:
        continue
    # Option 1: complementary interval ends before l -> use end_best at position l-1 for duration d2
    end_best = pre_end[d2]
    if l-1 >= 0:
        cost2 = end_best[l-1]
        if cost2 < INF:
            total = c + cost2
            if total < ans:
                ans = total
    # Option 2: complementary interval starts after r -> use start_best at position r+1
    start_best = pre_start[d2]
    if r+1 < LIMIT:
        cost2 = start_best[r+1]
        if cost2 < INF:
            total = c + cost2
            if total < ans:
                ans = total

if ans >= INF:
    print(-1)
else:
    print(ans)

