import sys
from math import isqrt

def precompute(max_n):
    KMAX = int(round(max_n ** (1/3)))  # 1e6 for 1e18
    S = set()
    limit = max_n
    for k in range(2, KMAX + 1):
        s = 1
        # build sums: after i iterations s = sum_{j=0..i} k^j
        for i in range(1, 100):  # 100 is safe upper bound (won't reach)
            s = s * k + 1
            if s > limit:
                break
            if i >= 2:
                S.add(s)
    return S

def check_m2(n):
    # check n = 1 + k + k^2 -> k^2 + k + 1 - n = 0
    # discriminant D = 4n - 3, k = (-1 + sqrt(D)) / 2
    D = 4 * n - 3
    if D <= 0:
        return False
    s = isqrt(D)
    if s * s != D:
        return False
    if (s - 1) % 2 != 0:
        return False
    k = (s - 1) // 2
    return k > 1

