import sys
import re
from itertools import product

def parse_expression(expr):
    tokens = []
    i = 0
    while i < len(expr):
        if expr[i] == '_':
            j = i
            while j < len(expr) and expr[j] == '_':
                j += 1
            tokens.append(('var', i, j))
            i = j
        else:
            tokens.append(('op', expr[i]))
            i += 1
    return tokens

def build_expression_tree(tokens):
    variables = []
    ops = []
    for token in tokens:
        if token[0] == 'var':
            variables.append(len(variables))
        else:
            ops.append(token[1])
    return variables, ops

def evaluate_expression(values, variables, ops):
    stack = []
    for i in range(len(variables)):
        stack.append(values[variables[i]])
    for op in ops:
        if op == '-':
            b = stack.pop()
            a = stack.pop()
            stack.append(a - b)
        elif op == '/':
            b = stack.pop()
            if b == 0:
                return None  # division by zero
            a = stack.pop()
            stack.append(a // b)
        elif op == '*':
            b = stack.pop()
            a = stack.pop()
            stack.append(a * b)
        elif op == '+':
            b = stack.pop()
            a = stack.pop()
            stack.append(a + b)
        elif op == ':':
            pass  # no operation, just part of the structure
    return stack[0]

def parse_constraint(constraint):
    parts = re.split(r'([<>=]+)', constraint)
    lhs = []
    rhs = []
    i = 0
    while i < len(parts):
        if parts[i] == '':
            i += 1
            continue
        if parts[i] in ['<', '>', '=', '<=', '>=', '==']:
            op = parts[i]
            i += 1
            break
        else:
            lhs.append(parts[i])
            i += 1
    while i < len(parts):
        rhs.append(parts[i])
        i += 1
    lhs_str = ''.join(lhs)
    rhs_str = ''.join(rhs)
    lhs_vars = []
    for c in lhs_str:
        if c == '_':
