def longest_substring_twice(s):
    n = len(s)
    seen = set()
    max_len = 0

    for i in range(n):
        for j in range(i + 1, n + 1):
            substr = s[i:j]
            if substr in seen:
                max_len = max(max_len, j - i)
            seen.add(substr)
    return max_len

