def minimum_ternary_string(s):
    zeros = []
    ones = []
    twos = []

    for char in s:
        if char == '0':
            zeros.append(char)
        elif char == '1':
            ones.append(char)
        else:
            twos.append(char)

    result = ''.join(zeros + ones + twos)
    return result

# Read input and process
import sys

def main():
    inputs = sys.stdin.read().split()
    for s in inputs:
        print(minimum_ternary_string(s))

if __name__ == "__main__":
    main()
