import sys
import math

def is_snowflake(n):
    if n == 1:
        return False
    # We need to check if there exists k > 1 such that n can be represented as a snowflake
    # A snowflake is constructed in layers. The formula for the number of vertices after m layers is:
    # n = 1 + k * (k^m - 1) / (k - 1)
    # We need to find if such k and m exist with k > 1, m >= 1
    # Try all possible k from 2 up to sqrt(n), since k^m grows quickly
    max_k = int(n ** 0.5) + 2
    for k in range(2, max_k + 1):
        # For each k, try to find m such that 1 + k * (k^m - 1) // (k - 1) == n
        # This is equivalent to solving: (k^m - 1) == (n - 1) * (k - 1) / k
        # So we need (n - 1) * (k - 1) % k == 0
        if (n - 1) % k != 0:
            continue
        total = (n - 1) * (k - 1) // k + 1
        if total == n:
            return True
        # Now check if total is of the form 1 + k * (k^m - 1) / (k - 1)
        # We can binary search m
        low = 1
        high = 60  # since k^60 is way larger than 1e18
        while low <= high:
            mid = (low + high) // 2
            # Compute 1 + k * (k^mid - 1) // (k - 1)
            try:
                term = (pow(k, mid) - 1) // (k - 1)
                val = 1 + k * term
                if val == n:
                    return True
                elif val < n:
                    low = mid + 1
                else:
                    high = mid - 1
            except:
                high = mid - 1
    return False

def main():
    input = sys.stdin.read
    data = input().split()
    t = int(data[0])
    results = []
    for i in range(1, t + 1):
        n = int(data[i])
        if is_snowflake(n):
            results.append("YES")
        else:
            results.append("NO")
    print('\n'.join(results))

if __name__ == "__main__":
    main()
