import sys

def check_string(s):
    count_a = s.count('a')
    count_b = s.count('b')
    count_c = s.count('c')

    # Check if there is at least one 'a' and one 'b'
    if count_a == 0 or count_b == 0:
        return "NO"

    # Check if number of 'c' equals number of 'a' or 'b'
    if count_c != count_a and count_c != count_b:
        return "NO"

    # Check the order: all 'a's first, then 'b's, then 'c's
    if not (s.startswith('a') and s.rstrip('abc').startswith('b')):
        return "NO"

    return "YES"

def main():
    s = sys.stdin.readline().strip()
    print(check_string(s))

if __name__ == "__main__":
    main()
