import sys

def is_valid_string(s):
    count_a = s.count('a')
    count_b = s.count('b')
    count_c = s.count('c')

    # Check if there are at least one 'a' and one 'b'
    if count_a == 0 or count_b == 0:
        return False

    # Check if the number of 'c' matches either count_a or count_b
    if count_c != count_a and count_c != count_b:
        return False

    # Check the order: all 'a's first, then 'b's, then 'c's
    a_part = s[:count_a]
    b_part = s[count_a:count_a + count_b]
    c_part = s[count_a + count_b:]

    if a_part != 'a' * count_a or b_part != 'b' * count_b or c_part != 'c' * count_c:
        return False

    return True

def main():
    s = sys.stdin.readline().strip()
    if is_valid_string(s):
        print("YES")
    else:
        print("NO")

if __name__ == "__main__":
    main()
