import sys
input = sys.stdin.read

MOD = 998244353

def main():
    data = input().strip().split()
    N = int(data[0])
    K = int(data[1])

    if N == 0:
        print(1)
        return

    # dp[i][j] = number of ways to form i sets with maximum value j
    dp = [[0] * (K + 2) for _ in range(N + 1)]

    # Base case: one set with max value j
    for j in range(K + 1):
        dp[1][j] = 1 << j  # 2^j ways to choose subsets with max value <= j

    for i in range(2, N + 1):
        for j in range(K + 1):
            # For each possible previous max value less than j
            total = 0
            for prev_j in range(j):
                total += dp[i - 1][prev_j]
                if total >= MOD:
                    total -= MOD
            dp[i][j] = total

    # Sum all possibilities for the last set
    result = 0
    for j in range(K + 1):
        result += dp[N][j]
        if result >= MOD:
            result -= MOD

    print(result % MOD)

if __name__ == "__main__":
    main()
