import sys

def get_intersection(line1, line2):
    a1, b1, c1 = line1
    a2, b2, c2 = line2
    denominator = a1 * b2 - a2 * b1
    if denominator == 0:
        return None
    x = (b1 * c2 - b2 * c1) / denominator
    y = (a2 * c1 - a1 * c2) / denominator
    return x, y

def get_distance(point1, point2):
    x1, y1 = point1
    x2, y2 = point2
    return ((x2 - x1) ** 2 + (y2 - y1) ** 2) ** 0.5

def get_block_number(point, lines):
    block_number = 1
    for line in lines:
        x, y = point
        a, b, c = line
        if a * x + b * y + c > 0:
            block_number += 1
    return block_number

def main():
    home = list(map(int, input().split()))
    university = list(map(int, input().split()))
    n = int(input())
    lines = []
    for _ in range(n):
        lines.append(list(map(int, input().split())))
    home_block_number = get_block_number(home, lines)
    university_block_number = get_block_number(university, lines)
    intersections = set()
    for i in range(n):
        for j in range(i + 1, n):
            intersection = get_intersection(lines[i], lines[j])
            if intersection is not None:
                intersections.add(intersection)
    intersection_blocks = {}
    for intersection in intersections:
        intersection_blocks[intersection] = get_block_number(intersection, lines)
    min_steps = float('inf')
    for intersection in intersections:
        distance_home = get_distance(home, intersection)
        distance_university = get_distance(university, intersection)
        steps = distance_home + distance_university
        block_number = intersection_blocks[intersection]
        if block_number == home_block_number or block_number == university_block_number:
            continue
        steps += 1
        min_steps = min(min_steps, steps)
    print(min_steps)

if __name__ == "__main__":
    main()
