MOD = 998244353

def main():
    n, k, f = map(int, input().split())

    dp = [[0] * (k + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        prefix_sum = [0] * (k + 1)
        for j in range(k + 1):
            prefix_sum[j] = (prefix_sum[j - 1] + dp[i - 1][j]) % MOD

        for j in range(k + 1):
            if j <= k:
                dp[i][j] = prefix_sum[j] * j % MOD
            dp[i][j] += prefix_sum[k] - prefix_sum[max(0, k - 2 * (i - 1))]
            dp[i][j] %= MOD

    ans = 0
    for i in range(min(k + 1, f + 1)):
        ans += dp[n][i]
        ans %= MOD

    print(ans)

if __name__ == "__main__":
    main()
