def twisty_movement():
    n = int(input())
    a = list(map(int, input().split()))

    dp1 = [0] * (n + 1)
    dp2 = [0] * (n + 1)

    for i in range(1, n + 1):
        if a[i - 1] == 1:
            dp1[i] = max(dp1[i], dp1[i - 1])
            dp2[i] = max(dp2[i], dp2[i - 1])
        else:
            dp1[i] = max(dp1[i], dp2[i - 1] + 1)
            dp2[i] = max(dp2[i], dp1[i - 1])

    ans = max(dp1[n], dp2[n])
    print(ans)


if __name__ == "__main__":
    t = 1  # number of test cases
    for _ in range(t):
        twisty_movement()
