MOD = 1000000007

def count_beautiful_strings(n, m, g):
    if n > m and g == 1 or n < m and g == 0:
        return 0

    total = 0
    for i in range(min(n, m) + 1):
        j = min(n, m) - i
        k = max(n, m) - j
        total += (n - i) * (m - j)

    return total % MOD

def main():
    n, m, g = map(int, input().split())
    result = count_beautiful_strings(n, m, g)
    print(result)

if __name__ == "__main__":
    main()
