MOD = 10**9 + 7

def solve():
    s = input()
    n = int(input())
    mapping = {str(i): str(i) for i in range(10)}

    for _ in range(n):
        query = input().split('->')
        digit = query[0]
        new_value = query[1] if len(query) > 1 else ''

        # Update the mapping
        for i in range(10):
            mapping[str(i)] = mapping[str(i)].replace(digit, new_value)

    # Calculate the result
    result = 0
    for char in s:
        result = (result * 10 + int(mapping[char])) % MOD

    print(result)

if __name__ == "__main__":
    t = 1  # number of test cases
    for _ in range(t):
        solve()
