import sys

def solve(n, x, cards):
    total = sum(cards)
    if total == 0:
        return 0
    elif abs(total) <= n:
        return 1
    else:
        diff = abs(total) - n
        return 2 + (diff // (2 * x)) + (1 if diff % (2 * x) > 0 else 0)

n, x = map(int, input().split())
cards = list(map(int, input().split()))

print(solve(n, x, cards))
