def find_minimum_cards():
    n, x = map(int, input().split())
    found_cards = list(map(int, input().split()))

    # Create a dictionary to store the count of each number
    card_counts = {}
    for card in found_cards:
        if card in card_counts:
            card_counts[card] += 1
        else:
            card_counts[card] = 1

    # Calculate the net sum of the found cards
    net_sum = sum(found_cards)

    # If the net sum is zero, no additional cards are needed
    if net_sum == 0:
        return 0

    # Find the minimum number of cards needed to make the net sum zero
    min_cards = float('inf')
    for i in range(-x, x + 1):
        if i not in card_counts:
            # If we need to add a card with value i, increment the count by 1
            card_counts[i] = 1
        else:
            # If we already have a card with value i, increment its count by 1
            card_counts[i] += 1

        # Calculate the net sum after adding the card with value i
        net_sum_after_adding = net_sum + i * card_counts[i]

        # If the net sum is now zero, update the minimum number of cards
        if net_sum_after_adding == 0:
            min_cards = min(min_cards, card_counts[i])

        # If the net sum is now positive, we need to remove cards with value -i
        elif net_sum_after_adding > 0:
            # Update the count of cards with value -i
            card_counts[-i] -= 1

            # If the count of cards with value -i becomes zero, remove it from the dictionary
            if card_counts[-i] == 0:
                del card_counts[-i]

    # Return the minimum number of cards needed
    return min_cards


