def find_minimum_cards():
    n, x = map(int, input().split())
    found_cards = list(map(int, input().split()))

    # Create a dictionary to store the count of each number
    card_counts = {}
    for card in found_cards:
        if card in card_counts:
            card_counts[card] += 1
        else:
            card_counts[card] = 1

    # Calculate the net sum of the found cards
    net_sum = sum(found_cards)

    # Initialize variables to track the minimum number of cards needed
    min_cards_needed = 0

    # If the net sum is not zero, we need to find more cards to make it zero
    if net_sum != 0:
        # We need to find cards that can make the net sum zero
        # We can do this by finding the maximum positive and negative numbers
        max_positive = max(0, -net_sum)
        max_negative = max(0, net_sum)

        # The maximum number of cards we need to find is the maximum of the maximum positive and negative numbers
        min_cards_needed = max(max_positive, max_negative)

        # We also need to consider the cards that are already found but have a count greater than the minimum number of cards needed
        # In this case, we only need to consider the cards with the maximum positive or negative value
        for card, count in card_counts.items():
            if (card > 0 and count >= min_cards_needed) or (card < 0 and count >= min_cards_needed):
                min_cards_needed = min(min_cards_needed, count)

    return str(min_cards_needed)


if __name__ == "__main__":
    print(find_minimum_cards())
