import sys

def find_max():
    n = int(input())
    a = list(map(int, input().split()))
    m_str = input()
    m = 0
    for i in range(n):
        m += int(m_str[i]) * (2 ** (n - 1 - i))

    max_val = 0
    for i in range(n):
        curr_val = 0
        for j in range(n):
            if (m & (1 << j)) and (not (i & (1 << j))):
                curr_val += a[j]
        max_val = max(max_val, curr_val)

    print(max_val)

if __name__ == "__main__":
    t = 1
    while t > 0:
        find_max()
        t -= 1
