MOD = 998244353

n = int(input())

if n == 0:
    print(0)
else:
    a, b = 1, 1
    if n == 1:
        fib = a
    elif n == 2:
        fib = b
    else:
        for _ in range(3, n + 1):
            c = (a + b) % MOD
            a, b = b, c
        fib = b

    pow_2 = pow(2, n, MOD)
    inv_pow_2 = pow(pow_2, MOD - 2, MOD)
    res = (fib * inv_pow_2) % MOD
    print(res)
