import sys

def main():
    mod = 998244353
    inv2 = pow(2, -1, mod)
    n = int(input())

    def power(x, y):
        res = 1
        while y:
            if y & 1:
                res = res * x % mod
            x = x * x % mod
            y >>= 1
        return res

    def comb(n, m):
        if m < 0 or m > n:
            return 0
        return fac[n] * inv[n-m] % mod * inv[m] % mod

    fac = [1]
    inv = [1]
    for i in range(1, n + 1):
        fac.append(fac[-1] * i % mod)
        inv.append(power(fac[-1], mod - 2))

    f = [[0] * (n + 1) for _ in range(n + 1)]
    f[0][0] = 1
    for i in range(1, n + 1):
        for j in range(i + 1):
            f[i][j] = (f[i-1][j] * inv2 + f[i-1][j-1] * inv2) % mod
    ans = 0
    for i in range(n + 1):
        ans += f[n][i] * comb(n, i) % mod * comb(i, n-i) % mod
        ans %= mod
    print(ans)

if __name__ == "__main__":
    main()
