def check_string(s):
    n = len(s)
    a_count = s.count('a')
    b_count = s.count('b')

    for c_count in [a_count, b_count, min(a_count, b_count)]:
        if c_count == 0:
            continue

        prefix = s[:n - c_count]
        suffix = s[n - c_count:]

        if sorted(prefix) == sorted(suffix):
            return "YES"

    return "NO"


