import sys

def read_input():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    return n, a

def solve(n, a):
    max_len = 1
    for i in range(1, n):
        if a[i] > a[i - 1]:
            max_len += 1
        else:
            max_len = 1
