def longest_increasing_subsequence(arr):
    n = len(arr)
    max_length = 1
    for i in range(n - 1):
        count = 1
        for j in range(i + 1, n):
            if arr[j] > arr[j - 1]:
                count += 1
            else:
                count = 2
            max_length = max(max_length, count)
    return max_length

