import sys

def main():
    mod = 998244353
    n = int(input())
    if n % 2 == 0:
        print(0)
        return

    fac = [1] * (n + 1)
    inv = [1] * (n + 1)
    for i in range(1, n + 1):
        fac[i] = fac[i - 1] * i % mod
        inv[i] = pow(fac[i], mod - 2, mod)

    def C(n, m):
        if n < m:
            return 0
        return fac[n] * inv[m] % mod * inv[n - m] % mod

    ans = 0
    for i in range(1, n):
        for j in range(i + 1, n):
            if (j - i) % 2 == 0:
                ans += (-1) ** ((i - 1) // 2) * (-1) ** ((j - 1) // 2) * C(n - 2, i - 1) * fac[i - 1] * fac[j - i - 1] * fac[n - j - 1] % mod
                ans %= mod
    print(ans)

if __name__ == "__main__":
    main()
