import sys

def orientation(p, q, r):
    """Find the orientation of three points (p, q, r)."""
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0  # Collinear
    return 1 if val > 0 else 2  # Clockwise or counterclockwise

def convex_hull(points):
    """Find the convex hull of a set of 2D points."""
    n = len(points)
    if n < 3:
        return points

    hull = []
    l = 0
    for i in range(1, n):
        if points[i][0] < points[l][0]:
            l = i

    p = l
    while True:
        hull.append(points[p])
        q = (p + 1) % n

        for i in range(n):
            if orientation(points[p], points[i], points[q]) == 2:
                q = i

        p = q
        if p == l:
            break

    return hull

def can_assign(rebels, bases):
    """Check if it's possible to assign rebels to bases without collision."""
    points = rebels + bases
    hull = convex_hull(points)

    # Check if there are more bases than rebels in the convex hull
    num_bases_in_hull = sum(1 for point in bases if point in hull)
    return num_bases_in_hull <= len(rebels)

def main():
    lines = sys.stdin.read().strip().split('\n')
    lines = [line.split(' ') for line in lines]

    R, B = map(int, lines[0])
    rebels = []
    bases = []

    for i in range(1, R + 1):
        rebels.append((int(lines[i][0]), int(lines[i][1])))

    for i in range(R + 1, R + B + 1):
        bases.append((int(lines[i][0]), int(lines[i][1])))

    if can_assign(rebels, bases):
        print('Yes')
    else:
        print('No')

if __name__ == "__main__":
    main()
