import sys

def orientation(p, q, r):
    """Find the orientation of three points (p, q, r)."""
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0  # Collinear
    return 1 if val > 0 else 2  # Clockwise or counterclockwise

def convex_hull(points):
    """Find the convex hull of a set of 2D points."""
    n = len(points)
    if n < 3:
        return points

    hull = []
    l = 0
    for i in range(1, n):
        if points[i][0] < points[l][0]:
            l = i

    p = l
    while True:
        hull.append(points[p])
        q = (p + 1) % n

        for i in range(n):
            if orientation(points[p], points[i], points[q]) == 2:
                q = i

        p = q
        if p == l:
            break

    return hull

def main():
    input_lines = sys.stdin.readlines()
    results = []

    for i in range(0, len(input_lines), 2):
        R, B = map(int, input_lines[i].strip().split())
        spaceships = []
        bases = []

        for j in range(R):
            x, y = map(int, input_lines[i + 1 + j].strip().split())
            spaceships.append((x, y))

        for j in range(R, R + B):
            x, y = map(int, input_lines[i + 1 + j].strip().split())
            bases.append((x, y))

        combined_points = spaceships + bases
        hull = convex_hull(combined_points)

        result = "Yes" if len(hull) == R + B else "No"
        results.append(result)

    print("\n".join(results))

if __name__ == "__main__":
    main()
