import sys

MOD = 998244353

def fast_pow(a, n):
    res = 1
    while n > 0:
        if n % 2 == 1:
            res = (res * a) % MOD
        a = (a * a) % MOD
        n //= 2
    return res

def main():
    input_lines = sys.stdin.readlines()
    for line in input_lines[1:]:
        N, K = map(int, line.split())
        dp = [[0] * (K + 1) for _ in range(N + 1)]
        dp[0][0] = 1
        for i in range(1, N + 1):
            for j in range(K + 1):
                for k in range(j + 1):
                    dp[i][j] += dp[i - 1][j - k] * fast_pow(k + 1, K - j)
                    dp[i][j] %= MOD
        print(sum(dp[N]) % MOD)

if __name__ == "__main__":
