import sys

def solve():
    x1, y1 = map(int, sys.stdin.readline().split())
    x2, y2 = map(int, sys.stdin.readline().split())
    n = int(sys.stdin.readline())

    roads = []
    for _ in range(n):
        a, b, c = map(int, sys.stdin.readline().split())
        roads.append((a, b, c))

    def get_side(a, b, c, x, y):
        val = a * x + b * y + c
        if val > 0:
            return 1
        elif val < 0:
            return -1
        else:
            return 0 # Should not happen based on problem statement

    steps = 0
    for a, b, c in roads:
        side1 = get_side(a, b, c, x1, y1)
        side2 = get_side(a, b, c, x2, y2)

        if side1 != side2:
            steps += 1

    sys.stdout.write(str(steps) + '\n')

solve()
