def solve():
    n = int(input())
    a = list(map(int, input().split()))

    max_lns = 0

    for l in range(n + 1):
        for r in range(l, n + 1):
            temp_a = list(a)

            if l < r:
                sub_array = temp_a[l:r]
                sub_array.reverse()
                temp_a[l:r] = sub_array


            ones_prefix = [0] * (n + 1)
            for i in range(n):
                ones_prefix[i+1] = ones_prefix[i] + (1 if temp_a[i] == 1 else 0)

            twos_suffix = [0] * (n + 1)
            for i in range(n - 1, -1, -1):
                twos_suffix[i] = twos_suffix[i+1] + (1 if temp_a[i] == 2 else 0)


            current_lns = 0
            for i in range(n + 1): 
                current_lns = max(current_lns, ones_prefix[i] + twos_suffix[i])

            max_lns = max(max_lns, current_lns)

    print(max_lns)

solve()
