import sys

def solve():
    n, m, g = map(int, sys.stdin.readline().split())
    MOD = 1000000007

    fact = [1] * (n + m + 1)
    inv_fact = [1] * (n + m + 1)

    for i in range(1, n + m + 1):
        fact[i] = (fact[i-1] * i) % MOD

    inv_fact[n + m] = pow(fact[n + m], MOD - 2, MOD)
    for i in range(n + m - 1, -1, -1):
        inv_fact[i] = (inv_fact[i+1] * (i+1)) % MOD

    def nCr_mod_p(n_val, r_val):
        if r_val < 0 or r_val > n_val:
            return 0
        num = fact[n_val]
        den = (inv_fact[r_val] * inv_fact[n_val - r_val]) % MOD
        return (num * den) % MOD

    ans = 0

    if g == 0:
        # If m == 0, the string is all zeros. The final character is 0 if n >= 1.
        if m == 0:
            if n > 0:
                ans = 1
            else:
                ans = 0 # n=0, m=0, g=0, n+m >= 1 is false
        else:
            # For g=0, the number of 1s must be odd.
            # The number of 1s in the final string is m % 2.
            # So we need m % 2 == 1.
            # If m is odd, then any string with n zeros and m ones will result in 0.
            if m % 2 == 1:
                ans = nCr_mod_p(n + m, n)
            else:
                ans = 0
    else: # g == 1
        # If m == 0, the string is all zeros. The final character is 0. So cannot be 1.
        if m == 0:
            ans = 0
        else:
            # For g=1, the number of 1s must be even.
            # The number of 1s in the final string is m % 2.
            # So we need m % 2 == 0.
            # If m is even, then any string with n zeros and m ones will result in 1.
            if m % 2 == 0:
                ans = nCr_mod_p(n + m, n)
            else:
                ans = 0

    sys.stdout.write(str(ans) + '\n')

solve()

