import sys
import ast
from itertools import permutations

notes_order = ['C','C#','D','D#','E','F','F#','G','G#','A','B','H']
note_to_idx = {n:i for i,n in enumerate(notes_order)}

def classify(chord_str):
    parts = chord_str.strip().split()
    if len(parts) != 3:
        return "strange"
    idxs = []
    for p in parts:
        if p not in note_to_idx:
            return "strange"
        idxs.append(note_to_idx[p])
    for perm in permutations(idxs,3):
        a,b,c = perm
        d1 = (b - a) % 12
        d2 = (c - b) % 12
        if d1 == 4 and d2 == 3:
            return "major"
        if d1 == 3 and d2 == 4:
            return "minor"
    return "strange"

def main():
    data = sys.stdin.read().strip()
    if not data:
        return
    data = data.strip()
    results = []
    # Try parse as Python literal list of strings
    try:
        lit = ast.literal_eval(data)
        if isinstance(lit, list) and all(isinstance(x,str) for x in lit):
            for item in lit:
                results.append(classify(item))
        else:
            # treat as single line
            results.append(classify(data))
    except Exception:
        # fallback: treat input as lines, or single chord
        lines = [line.strip() for line in data.splitlines() if line.strip()]
        if len(lines) == 1:
            # maybe single chord tokens
            results.append(classify(lines[0]))
        else:
            for line in lines:
                results.append(classify(line))
    # Print outputs: one per line
    print("\n".join(results))

if __name__ == "__main__":
    main()

