    for t in triad:
        if t not in note_to_int:
            return "strange"
        vals.append(note_to_int[t])
    for a,b,c in permutations(vals, 3):
        x = (b - a) % 12
        y = (c - b) % 12
        if x == 4 and y == 3:
            return "major"
        if x == 3 and y == 4:
            return "minor"
    return "strange"

data = sys.stdin.read().strip()
if not data:
    sys.exit(0)

