if len(lines) == 1:
    line = lines[0]
    # handle Python-like list: ['C E G', 'C# B F', 'A B H']
    if line.startswith('[') and "'" in line:
        extracted = re.findall(r"'([^']*)'", line)
        for s in extracted:
            parts = s.split()
            if len(parts) == 3:
                cases.append(parts)
    elif line.startswith('[') and '"' in line:
        extracted = re.findall(r'"([^"]*)"', line)
        for s in extracted:
            parts = s.split()
            if len(parts) == 3:
                cases.append(parts)
    else:
        parts = line.split()
        if len(parts) == 3:
            cases.append(parts)
        else:
            # maybe multiple triads given sequentially, take every 3 tokens
            toks = parts
            if len(toks) % 3 == 0:
                for i in range(0, len(toks), 3):
                    cases.append(toks[i:i+3])
            else:
                # fallback: treat whole line as one case if possible by commas
                parts2 = re.split(r'[,\s]+', line)
                if len(parts2) >= 3:
                    for i in range(0, len(parts2), 3):
                        if i+2 < len(parts2):
                            cases.append(parts2[i:i+3])
else:
    for line in lines:
        parts = line.split()
        if len(parts) == 3:
            cases.append(parts)
        else:
            # try to extract quoted segments in this line
            extracted = re.findall(r"'([^']*)'", line)
            if not extracted:
                extracted = re.findall(r'"([^"]*)"', line)
            if extracted:
                for s in extracted:
                    p = s.split()
                    if len(p) == 3:
                        cases.append(p)

results = [classify(case) for case in cases]
if not results:
    sys.exit(0)
print("\n".join(results))

