s = sys.stdin.read()
if not s:
    sys.exit(0)
pattern = r"C#|D#|F#|G#|C|D|E|F|G|A|B|H"
notes = re.findall(pattern, s)
order = ['C','C#','D','D#','E','F','F#','G','G#','A','B','H']
idx = {n:i for i,n in enumerate(order)}

def classify(triple):
    vals = [idx[t] for t in triple]
    for i in range(3):
        r = vals[i]
        others = [vals[j] for j in range(3) if j!=i]
        ds = sorted(((x - r) % 12) for x in others)
        a = ds[0]
        b = (ds[1] - ds[0]) % 12
        if a == 4 and b == 3:
            return "major"
        if a == 3 and b == 4:
            return "minor"
    return "strange"

results = []
# Try per-line triples first
lines = s.strip().splitlines()
