import sys, ast
from itertools import permutations

s = sys.stdin.read().strip()
if not s:
    sys.exit(0)

# Try to parse Python-style list input, otherwise treat as raw lines
try:
    parsed = ast.literal_eval(s)
    if isinstance(parsed, list):
        raw_lines = [str(x) for x in parsed]
    else:
        raw_lines = [str(parsed)]
except Exception:
    raw_lines = s.splitlines()

# Split possible comma-separated cases on each line
cases = []
for line in raw_lines:
    for part in line.split(','):
        p = part.strip().strip('\'"')
        if p:
            cases.append(p)

notes = ["C","C#","D","D#","E","F","F#","G","G#","A","B","H"]
idx = {n:i for i,n in enumerate(notes)}

def classify(triple):
    toks = triple.split()
    if len(toks) != 3:
        return None
    try:
        ids = [idx[t] for t in toks]
    except KeyError:
        return None
    for perm in permutations(range(3)):
        a,b,c = ids[perm[0]], ids[perm[1]], ids[perm[2]]
        d1 = (b - a) % 12
        d2 = (c - b) % 12
        if d1 == 4 and d2 == 3:
            return "major"
        if d1 == 3 and d2 == 4:
            return "minor"
    return "strange"

results = []
for c in cases:
    res = classify(c)
    if res is not None:
        results.append(res)

if not results:
    sys.exit(0)
if len(results) == 1:
    print(results[0])
else:
    for r in results:
        print(r)

