data = sys.stdin.read().splitlines()
ptr = 0
out_lines = []

while ptr < len(data):
    if data[ptr].strip() == "":
        ptr += 1
        continue
    n = int(data[ptr].strip())
    ptr += 1
    arr = []
    for _ in range(2 * n - 2):
        arr.append(data[ptr].strip())
        ptr += 1

    # find the two strings of length n-1
    cand = [s for s in arr if len(s) == n - 1]
    a, b = cand[0], cand[1]

    def try_build(pref_big, suff_big):
        s = pref_big + suff_big[-1]
        # prepare prefix and suffix for each length
        pref = {l: s[:l] for l in range(1, n)}
        suff = {l: s[n - l:] for l in range(1, n)}
        # for lengths where pref==suff we need counts
        used_pref_for_len = {l: 0 for l in range(1, n)}
        labels = []
        for t in arr:
            l = len(t)
            if t == pref[l] and t == suff[l]:
                # ambiguous, assign P if not used yet, else S
                if used_pref_for_len[l] == 0:
                    labels.append('P')
                    used_pref_for_len[l] = 1
                else:
                    labels.append('S')
            elif t == pref[l]:
                labels.append('P')
            elif t == suff[l]:
                labels.append('S')
            else:
                return None  # invalid
        return ''.join(labels)

    res = try_build(a, b)
    if res is None:
        res = try_build(b, a)
    if res is None:
        res = ""  # should not happen per problem statement
    out_lines.append(res)

sys.stdout.write("\n".join(out_lines))

