    # compute expected counts for prefix and suffix occurrences by string value
    pref_count = {}
    suff_count = {}
    for l in range(1, n):
        p = candidate[:l]
        s = candidate[n-l:]
        pref_count[p] = pref_count.get(p, 0) + 1
        suff_count[s] = suff_count.get(s, 0) + 1
    res = []
    for s in arr:
        if pref_count.get(s, 0) > 0:
            res.append('P')
            pref_count[s] -= 1
        elif suff_count.get(s, 0) > 0:
            res.append('S')
            suff_count[s] -= 1
        else:
            return None
    return ''.join(res)

# try both possibilities
cand1 = a + b[-1]
cand2 = b + a[-1]

