import sys

data = [line.strip() for line in sys.stdin.read().splitlines() if line.strip() != ""]
if not data:
    sys.exit(0)
n = int(data[0])
words = data[1:1 + 2 * n - 2]

candidates = [w for w in words if len(w) == n - 1]
a, b = candidates[0], candidates[1]
cand_options = [a + b[-1], b + a[-1]]

def try_candidate(s):
    need_p = {}
    need_s = {}
    for l in range(1, n):
        p = s[:l]
        q = s[n-l:]
        need_p[p] = need_p.get(p, 0) + 1
        need_s[q] = need_s.get(q, 0) + 1
    res = []
    for w in words:
        if need_p.get(w, 0) > 0:
            res.append('P')
            need_p[w] -= 1
        elif need_s.get(w, 0) > 0:
            res.append('S')
            need_s[w] -= 1
        else:
            return None
    return "".join(res)

for cand in cand_options:
    ans = try_candidate(cand)
    if ans is not None:
        print(ans)
        break

