import sys
from collections import defaultdict

def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return
    # find first non-empty line for n
    idx = 0
    while idx < len(data) and data[idx].strip() == "":
        idx += 1
    if idx >= len(data):
        return
    n = int(data[idx].strip())
    idx += 1
    parts = []
    while len(parts) < 2 * n - 2 and idx < len(data):
        line = data[idx].rstrip("\n")
        if line != "":
            parts.append(line.strip())
        idx += 1
    # now parts has 2n-2 strings
    # find the two of length n-1
    cand = [s for s in parts if len(s) == n-1]
    a, b = cand[0], cand[1]

    def try_build(s):
        pref = defaultdict(int)
        suf = defaultdict(int)
        for L in range(1, n):
            pref[s[:L]] += 1
            suf[s[n-L:]] += 1
        res = []
        for x in parts:
            if pref.get(x, 0) > 0:
                res.append('P')
                pref[x] -= 1
            elif suf.get(x, 0) > 0:
                res.append('S')
                suf[x] -= 1
            else:
                return None
        return "".join(res)

    s1 = a + b[-1]
    s2 = b + a[-1]
    ans = try_build(s1)
    if ans is None:
        ans = try_build(s2)
    if ans is None:
        # Should not happen per problem statement, but fallback
        ans = "P" * (len(parts)//2) + "S" * (len(parts)//2)
    print(ans)

if __name__ == "__main__":
    main()
