import sys

data = sys.stdin.read().split()
if not data:
    sys.exit(0)
n = int(data[0])
arr = data[1:1 + 2 * n - 2]

longs = [s for s in arr if len(s) == n - 1]
a, b = longs[0], longs[1]

def try_candidate(s):
    prefix = [''] * (n + 1)
    suffix = [''] * (n + 1)
    used_p = [False] * (n + 1)
    used_s = [False] * (n + 1)
    for l in range(1, n):
        prefix[l] = s[:l]
        suffix[l] = s[n - l:]
    res = []
    for t in arr:
        l = len(t)
        if t == prefix[l] and not used_p[l]:
            res.append('P')
            used_p[l] = True
        elif t == suffix[l] and not used_s[l]:
            res.append('S')
            used_s[l] = True
        else:
            return None
    return ''.join(res)

cand1 = a + b[-1]
cand2 = b + a[-1]

ans = try_candidate(cand1)
if ans is None:
    ans = try_candidate(cand2)
if ans is None:
    # As a fallback (should not be needed), try reversed priority when ambiguous
    def try_candidate_alt(s):
        prefix = [''] * (n + 1)
        suffix = [''] * (n + 1)
        used_p = [False] * (n + 1)
        used_s = [False] * (n + 1)
        for l in range(1, n):
            prefix[l] = s[:l]
            suffix[l] = s[n - l:]
        res = []
        for t in arr:
            l = len(t)
            if t == suffix[l] and not used_s[l]:
                res.append('S')
                used_s[l] = True
            elif t == prefix[l] and not used_p[l]:
                res.append('P')
                used_p[l] = True
            else:
                return None
        return ''.join(res)
    ans = try_candidate_alt(cand1)
    if ans is None:
        ans = try_candidate_alt(cand2)

print(ans)
